# Solution
def solution(s):
    """
    Checks if a string containing '(', ')', and '*' is a valid parentheses string.

    Args:
        s: The input string.

    Returns:
        True if the string is valid, False otherwise.
    """
    low = 0  # Minimum number of open parentheses needed
    high = 0 # Maximum number of open parentheses possible

    for char in s:
        if char == '(':
            low += 1
            high += 1
        elif char == ')':
            low -= 1
            high -= 1
        else:  # char == '*'
            low -= 1  # Treat '*' as ')' to minimize open parentheses
            high += 1  # Treat '*' as '(' to maximize open parentheses

        if high < 0:  # Too many closing parentheses, invalid
            return False

        low = max(low, 0)  # Ensure low doesn't become negative

    return low == 0  # If low is 0, all open parentheses can be closed

